import hashlib

__all__ = [
    "get_md5",
    "get_sha1",
    "get_sha224",
    "get_sha256",
    "get_sha384",
    "get_sha512"
]


def get_md5(arg) -> str:
    """
    生成md5
    :param arg:
    :return:
    """
    md5 = hashlib.md5()
    md5.update(arg.encode(encoding='utf-8'))
    return md5.hexdigest()


# SHA
def get_sha1(arg) -> str:
    sha1 = hashlib.sha1()
    arg = arg.encode(encoding='utf-8')
    sha1.update(arg)
    return sha1.hexdigest()


def get_sha224(arg) -> str:
    return hashlib.sha224(
        arg.encode(encoding='utf-8')).hexdigest()


def get_sha256(arg) -> str:
    sha256 = hashlib.sha256()
    arg = arg.encode(encoding='utf-8')
    sha256.update(arg)
    return sha256.hexdigest()


def get_sha384(arg) -> str:
    return hashlib.sha384(
        arg.encode(encoding='utf-8')).hexdigest()


def get_sha512(arg) -> str:
    return hashlib.sha512(
        arg.encode(encoding='utf-8')).hexdigest()


# ###################### 有趣的编码加解密 开始 #########################
def custom_decode(s: str) -> str:
    """
    有趣的解密
    https://www.360kuai.com/92b1dcd97b7b48e6c
    """
    r = ""
    for t in range(len(s)):
        r += chr(ord(s[t]) - t % 2)
    return r


def custom_encode(s: str) -> str:
    """
    加密, 字母编码取余作为偏移
    """
    r = ""
    for t in range(len(s)):
        r += chr(ord(s[t]) + t % 2)
    return r
# ###################### 有趣的编码加解密 结束 #########################


